import React, { useEffect, useState } from "react";
import { useParams} from "react-router-dom";
import Http from "../../lib/request";
import "./Detail.css"

const DetailCharacter = () =>{
    const { episode_id } = useParams();
    const [films, setCharacter] = useState({})
    /*const [origin, setOrigin] = useState({})
    const [location, setLocation] = useState({})*/

    useEffect(()=>{
        const fetchCharacter = async ( ) =>{
            const films = await Http.instance.get_character(episode_id);
            setCharacter(films)
            //setOrigin(character.origin)
            //setLocation(character.location)
        }
        fetchCharacter();
    },[])

    return (
        <React.Fragment>
            <div className="Details">
                <div className="Details__container">
                    <h1>{films.title}</h1>
                    <div className="Details__information">
                        <div className="Details__img">
                            <img src={films.image} alt={films.name} />
                        </div>
                        <div className="Details__data">
                            <div className="Data__row">
                               <h3>Status: </h3> 
                                <p>{films.status}</p>
                            </div>
                            <div className="Data__row">
                               <h3>Species: </h3> 
                                <p>{films.species}</p>
                            </div>
                            <div className="Data__row">
                               <h3>Type: </h3> 
                                <p>{films.type || "NA"}</p>
                            </div>
                            <div className="Data__row">
                               <h3>Gender: </h3> 
                                <p>{films.release_date}</p>
                            </div>
                            
                        </div>
                    </div>
                </div>
            </div>
        </React.Fragment>
    );
};

export default DetailCharacter;
