    get_characters = async() =>{
        try{
            const request = await fetch (`${URL}`)
            const response = await request.json()
            return response.results
    
        }catch(err){
            throw new Error(err);
        }
    }
    get_character = async(id=1) => {
        try{
            const request = await fetch (`${URL}${id}`)
            const response = await request.json()
            return response
    
        }catch(err){
            throw new Error(err);
        }
    }
