    get_characters = async() =>{
        try{
            const request = await fetch (`${URL}`)
            const response = await request.json()
            return response.results
    
        }catch(err){
            throw new Error(err);
        }
    }
    get_character = async() => {
        try{
            const request = await fetch (`https://swapi.dev/api/films/4/`)
            const response = await request.json()
            return response
    
        }catch(err){
            throw new Error(err);
        }
    }
}
